dt-event.example:
var table = $('#example').DataTable( {
	columnReorder: true
} );

table.on( 'column-reorder', function ( e, settings, details ) {
	var headerCell = $( table.column( details.to ).header() );

	headerCell.addClass( 'reordered' );

	setTimeout( function () {
		headerCell.removeClass( 'reordered' );
	}, 2000 );
} );
