dt-example.js:
$(document).ready(function() {
	$('#example').DataTable( {
		dom: 'Bfrtip',
		colReorder: {
			enable: false
		},
		buttons: [
			{
				text: "Enable column reordering",
				action: function ( e, dt ) {
					dt.colReorder.enable();
				}
			},
			{
				text: "Disable column reordering",
				action: function ( e, dt ) {
					dt.colReorder.disable();
				}
			}
		]
	} );
} );
