dt-example.js:
$('#example').DataTable({
	scrollY: 200,
	paging: false,
	colReorder: {
		realtime: false
	},
	columns: [
		{width: '20%'},
		{width: '20%'},
		{width: '20%'},
		{width: '20%'},
		{width: '10%'},
		{width: '10%'},
	]
});

dt-example.js-vanilla:
new DataTable('#example', {
	scrollY: 200,
	paging: false,
	colReorder: {
		realtime: false
	}
});
