dt-example.js:
$('#example').DataTable({
	columnDefs: [
		{ orderable: false, targets: 0 },
		{ orderable: false, targets: -1 }
	],
	colReorder: {
		fixedColumnsLeft: 1,
		fixedColumnsRight: 1
	},
	fixedColumns: {
		left: 1,
		right: 1
	},
	ordering: [[1, 'asc']],
	scrollX: true,
	scrollCollapse: true
});

new DataTable('#example', {
	columnDefs: [
		{ orderable: false, target: 0 },
		{ orderable: false, target: -1 }
	],
	colReorder: {
		fixedColumnsLeft: 1,
		fixedColumnsRight: 1
	},
	fixedColumns: {
		left: 1,
		right: 1
	},
	ordering: [[1, 'asc']],
	scrollX: true,
	scrollCollapse: true
});
